<?php/** * Model class is to handle base DB calse */require_once 'database.php';class Model{    protected $dbServer = DB_HOST;    protected $dbUserName = DB_USER;    protected $dbPassword = DB_PASSWORD;    protected $dbDataBase = DB_NAME;    public $result;    public $connection;    public function __construct(){                $this->connection = new mysqli(                $this->dbServer,                $this->dbUserName,                $this->dbPassword,                $this->dbDataBase                );          if(mysqli_connect_errno())             {                   echo "Error: Could not connect to database.";                 exit;             }     }      public function data_query($query){        if($query){            $result = mysqli_query($query);        }else{            die('Error While Querieng Data base</br>');        }        return $result;         }           public function fetch_assoc($result){        return mysqli_fetch_assoc($result) ;    }        public function fetch_array($result){        return mysqli_fetch_array($result) ;    }   }